package string_methods;

public class Format {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(String.format("%d", 100));
		System.out.println(String.format("%f", 101.0));
		
		System.out.println(String.format("%s", "thiru"));
		System.out.println(String.format("%c", 't'));
		System.out.println(String.format("%x", 150));
		
		

	}

}
