package string_methods;

public class replaceAll {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String a="thirupathi reddy alla";
System.out.println(a.replaceAll("reddy", "goapl"));
System.out.println(a.replaceAll("\\s", ""));  //We use this method to remove the spaces
	}

}
